using Azure.Core;
using Microsoft.AspNetCore.Http;
using PostService.Dtos;
using System.Net;
using System.Net.Http.Headers;
using System.Text;
using System.Text.Json;

namespace AuthorizationServiceTests
{
    [TestClass]
    public class AuthorizationTests 
    {
        HttpClient _httpClient = new HttpClient();

        [TestMethod]
        public async Task CreatePost_RequiresAuthorization_ReturnsUnauthorizadWithoutToken()
        {
            // Arrange

            var request = new HttpRequestMessage(HttpMethod.Post, "http://34.141.52.221.nip.io/api/Post");
            PostCreateDto postCreateDto = new PostCreateDto();
            postCreateDto.Title = "test";
            request.Content = new StringContent("", Encoding.UTF8, "multipart/form-data");

            // Act
            var response = await _httpClient.SendAsync(request);

            // Assert
            Assert.AreEqual(HttpStatusCode.Unauthorized, response.StatusCode);
        }

        [TestMethod]
        public async Task CreatePost_RequiresAuthorization_ReturnsAuthorizedWithToken()
        {
            // Arrange
            var client = new HttpClient();
            client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..3jduLN-2gZR2VfxsANiqYxIWaEutKnLEV3Uh-eiFtsI");

            var stringContent = "";
            byte[] byteArray = Encoding.UTF8.GetBytes(stringContent);
            var fileContent = new ByteArrayContent(byteArray);
            fileContent.Headers.ContentType = MediaTypeHeaderValue.Parse("multipart/form-data");
            var postCreateDto = new MultipartFormDataContent
            {
                { new StringContent("Simple title"), "Title" },
                { fileContent, "ImageFile", "05a" }
            };

            // Act
            var response = await client.PostAsync("http://34.141.52.221.nip.io/api/Post", postCreateDto);

            // Assert
            Assert.AreEqual(HttpStatusCode.OK, response.StatusCode);
        }
    }
}